javapython <pre><code>import java.util.Stack;
class Solution {
    boolean solution(String s) {

        Stack&lt;Character&gt; charStack = new Stack&lt;&gt;();

        for (char c : s.toCharArray()){
            if (c == ')' &amp;&amp; !charStack.isEmpty()) {
                charStack.pop();
            } else {
                charStack.push(c);
            }
        }
        return charStack.isEmpty() ? true : false ;
    }
}
